mapper.select:
SELECT
COUNT(*) AS total
FROM
applicant a
LEFT JOIN `user` u ON u.id = a.user_id
LEFT JOIN class c ON c.id = a.class_id
LEFT JOIN `user` u1 ON u1.id = c.user_id


        AND c.user_id = #{userId}


        AND a.class_id = #{classId}


        AND a.user_id = #{userId}

SELECT u.account, u.user_name AS userName, u.phone AS studentPhone, c.class_name AS className, u1.user_name AS charger, u1.phone AS chargerPhone,
FROM applicant a LEFT JOIN `user` u ON u.id = a.user_id LEFT JOIN class c ON c.id = a.class_id LEFT JOIN `user` u1 ON u1.id = c.user_id
AND c.user_id = #{userId}
AND a.class_id = #{classId}
AND a.user_id = #{userId}
