mapper.select:
SELECT COUNT(*) AS total FROM homework h LEFT JOIN class c ON c.id = h.class_id LEFT JOIN `user` u ON u.id = h.creator LEFT JOIN user_subject us ON u.id = us.user_id LEFT JOIN `subject` s ON s.id = us.subject_id
AND h.creator = #{userId}
AND h.class_id = #{classId}

SELECT c.class_name AS className, u.user_name AS userName, s.subject_name AS subjectName,
FROM homework h LEFT JOIN class c ON c.id = h.class_id LEFT JOIN `user` u ON u.id = h.creator LEFT JOIN user_subject us ON u.id = us.user_id LEFT JOIN `subject` s ON s.id = us.subject_id
AND h.creator = #{userId}
AND h.class_id = #{classId}
ORDER BY h.id DESC LIMIT #{page},#{pageSize}

SELECT COUNT(*) AS total FROM homework h LEFT JOIN class c ON c.id = h.class_id LEFT JOIN `user` u ON u.id = h.creator LEFT JOIN user_subject us ON u.id = us.user_id LEFT JOIN `subject` s ON s.id = us.subject_id LEFT JOIN user_do_homework udh ON h.id = udh.homework_id
AND udh.user_id IS NULL
AND h.class_id = #{classId}
ORDER BY h.id DESC LIMIT #{page},#{pageSize}
