mapper.select:
SELECT COUNT(*) AS total FROM user_class uc LEFT JOIN `user` u ON uc.user_id = u.id LEFT JOIN user_role ur ON ur.user_id = u.id LEFT JOIN role r ON ur.role_id = r.id LEFT JOIN class c ON uc.class_id = c.id
AND uc.class_id IN ( SELECT uc.class_id FROM user_class uc WHERE uc.user_id = #{userId} )
AND uc.class_id = #{classId}

SELECT u.user_name AS userName, u.account, u.phone, CASE u.sex WHEN 0 THEN '男' WHEN 1 THEN '女' END AS sexName, u.sex AS sex, r.description, c.class_name AS className,
FROM user_class uc LEFT JOIN `user` u ON uc.user_id = u.id LEFT JOIN user_role ur ON ur.user_id = u.id LEFT JOIN role r ON ur.role_id = r.id LEFT JOIN class c ON uc.class_id = c.id
ur.role_id = 3 AND uc.class_id IN ( SELECT uc.class_id FROM user_class uc WHERE uc.user_id = #{userId} )
AND uc.class_id = #{classId}
ORDER BY uc.id DESC LIMIT #{page},#{pageSize}

SELECT c.class_name AS className,
FROM user_class uc LEFT JOIN `user` u ON uc.user_id = u.id LEFT JOIN class c ON c.id = uc.class_id
AND uc.user_id = #{userId}
