mapper.select:
SELECT c.id AS classId, c.class_name AS className, u1.user_name AS charger, s.subject_name AS subjectName, s.id AS subjectId,
FROM `user` u LEFT JOIN user_role ur ON u.id = ur.user_id LEFT JOIN user_class uc ON u.id = uc.user_id LEFT JOIN class c ON c.id = uc.class_id LEFT JOIN `user` u1 ON c.user_id = u1.id LEFT JOIN user_subject us ON us.user_id = u.id LEFT JOIN `subject` s ON us.subject_id = s.id
AND ur.role_id = #{roleId}
ORDER BY u.id DESC LIMIT #{page},#{pageSize}

SELECT u.id AS userId, u.user_name AS userName, uc.class_id AS classId, ur.role_id AS roleId FROM `user` u LEFT JOIN user_role ur ON u.id = ur.user_id LEFT JOIN user_class uc ON u.id = uc.user_id
AND u.account = #{account}
AND u.password = #{password}
