mapper.select:
SELECT COUNT(*) AS total FROM user_do_homework udh LEFT JOIN `user` u1 ON u1.id = udh.user_id LEFT JOIN homework h ON h.id = udh.homework_id LEFT JOIN `user` u ON u.id = h.creator LEFT JOIN user_subject us ON u.id = us.user_id LEFT JOIN `subject` s ON s.id = us.subject_id
AND udh.user_id = #{userId}
AND udh.homework_id = #{homeworkId}

SELECT h.title, h.answer, u.user_name AS userName, u1.user_name AS studentName, s.subject_name AS subjectName, h.class_id as classId,
FROM user_do_homework udh LEFT JOIN `user` u1 ON u1.id = udh.user_id LEFT JOIN homework h ON h.id = udh.homework_id LEFT JOIN `user` u ON u.id = h.creator LEFT JOIN user_subject us ON u.id = us.user_id LEFT JOIN `subject` s ON s.id = us.subject_id
AND udh.user_id = #{userId}
AND udh.homework_id = #{homeworkId}
ORDER BY udh.id DESC LIMIT #{page},#{pageSize}

mapper.update:
update user_do_homework
set mode = 1,
    score = #{score},
    remark = #{remark}
where user_id = #{userId} and homework_id = #{homeworkId}
