mapper.select:
SELECT
COUNT(*) AS total
FROM
video_total vt
LEFT JOIN `user` u ON u.id = vt.user_id
LEFT JOIN user_subject us ON u.id = us.user_id
LEFT JOIN `subject` s ON s.id = us.subject_id


        AND s.id = #{subjectId}


        AND uc.class_id = #{classId}


        AND vt.user_id = #{userId}

SELECT u.user_name AS userName, s.subject_name AS subjectName,
FROM video_total vt LEFT JOIN `user` u ON u.id = vt.user_id LEFT JOIN user_subject us ON u.id = us.user_id LEFT JOIN `subject` s ON s.id = us.subject_id LEFT JOIN user_class uc ON uc.user_id = vt.user_id
AND s.id = #{subjectId}
AND uc.class_id = #{classId}
AND vt.user_id = #{userId} GROUP BY vt.id
ORDER BY vt.id DESC LIMIT #{page},#{pageSize}
